const errorData = $('Parse Sentry Data').first().json;
const analysisRaw = $('Analyze Error').first().json.message?.content || $('Analyze Error').first().json.text || '{}';

let analysis;
try {
  analysis = JSON.parse(analysisRaw);
} catch (e) {
  analysis = {
    severity: 'medium',
    category: 'unknown',
    root_cause: 'ë¶„ì„ ì‹¤íŒ¨',
    solution: 'ìˆ˜ë™ í™•ì¸ í•„ìš”',
    affected_files: [],
    labels: ['bug']
  };
}

// í”„ë¡œì íŠ¸ â†’ Jira í”„ë¡œì íŠ¸ ë§¤í•‘
const projectMapping = {
  'product-hub': 'PROD',
  'producthub': 'PROD',
  'auth-hub': 'AUTH',
  'authhub': 'AUTH',
  'crawler': 'CRAWL',
  'mcp-server': 'MCP',
  'infrastructure': 'INFRA',
  'musinsa-api': 'MAPI'
};

const projectKey = errorData.project_slug?.toLowerCase() || errorData.project?.toLowerCase() || 'unknown';
const jiraProject = projectMapping[projectKey] || projectMapping[projectKey.replace(/-/g, '')] || 'DEV';

// Fingerprint ìƒì„± (ì¤‘ë³µ ì²´í¬ìš©)
const fingerprint = [
  errorData.project_slug || errorData.project,
  errorData.error_type || 'Error',
  errorData.culprit || 'unknown'
].join('-').replace(/[^a-zA-Z0-9-]/g, '_').substring(0, 100);

// Jira ìš°ì„ ìˆœìœ„ ë§¤í•‘
const priorityMap = {
  'critical': 'Highest',
  'high': 'High',
  'medium': 'Medium',
  'low': 'Low'
};

// Jira ë¼ë²¨ ìƒì„±
const jiraLabels = ['sentry-auto'];
if (analysis.category) jiraLabels.push(analysis.category);
if (analysis.severity === 'critical' || analysis.severity === 'high') jiraLabels.push('urgent');

return {
  errorData,
  analysis,
  jira: {
    project: jiraProject,
    fingerprint: fingerprint,
    priority: priorityMap[analysis.severity] || 'Medium',
    labels: jiraLabels,
    summary: `[Sentry] ${errorData.title.substring(0, 80)}`,
    search_jql: `project = ${jiraProject} AND summary ~ "${fingerprint}" AND status NOT IN (Done, Closed, Resolved) ORDER BY created DESC`
  }
};

const preparedData = $('Prepare Jira Data').first().json;
const searchResult = $('Search Existing Jira').first().json;
const issues = searchResult.issues || [];

const exists = issues.length > 0;
const existingIssue = exists ? issues[0] : null;

return {
  ...preparedData,
  jira: {
    ...preparedData.jira,
    exists: exists,
    existing_key: existingIssue?.key || null,
    existing_id: existingIssue?.id || null,
    existing_url: existingIssue ? `https://your-domain.atlassian.net/browse/${existingIssue.key}` : null
  }
};

